'use strict';
const express = require('express');
const articleParser = require('./index');

const app = express();
const port = 3000;

// 定义 GET /parse-article 路由
app.get('/parse-article', async (req, res) => {
    const { url } = req.query;
    if (!url) {
        return res.status(400).json({ error: 'URL is required' });
    }

    try {
        const result = await articleParser(url);
        res.json({ article: result });
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
});

// 启动服务器
app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
});

// 测试解析文章功能
articleParser('https://jonizquierdo.com/about/')
.then((result) => {
    console.log(result);
}).catch((reason) => {
    console.log(reason);
});
